$(function(){var t={};t.urls={index:{path:"/activities.json",method:"get"},create:{path:"/activities.json",method:"post"}},t.saveActivity=function(t){t.preventDefault();var i={};i.title=$("#activity_title").val(),i.body=$("#activity_body").val(),$.post(this.urls.create.path,{activity:i}).done(this.displayNewActivity)},t.displayActivities=function(t){$(t).each(function(t,i){var e=HandlebarsTemplates.activities(i);$("#activities").append(e)})},t.displayNewActivity=function(t){$("#activity_title").val(""),$("#activity_body").val("");var i=HandlebarsTemplates.activities(t);$("#activities").append(i)},t.getItems=function(){$.get(this.urls.index.path).done(this.displayActivities)},t.setEventHandlers=function(){$("#add_activity").on("submit",function(i){t.saveActivity(i)})},t.setEventHandlers(),t.getItems()});